import axios from "axios";
const localUrl = "http://localhost:5100";
const serverUrl = "https://exhi-lover.com";

export const fetchShowsbySearchQuery = async (searchQuery) => {
  try {
    const url = searchQuery ? `/api/shows/search/${searchQuery}` : "/api/shows";
    const response = await axios.get(`${localUrl}${url}`);
    console.log("hi", url);
    // const response = await axios.get(`${severUrl}${url}`);
    return response.data;
  } catch (error) {
    throw new Error("Failed to fetch shows");
  }
};
