/*global kakao*/
import React, { useEffect, useState } from "react";
import Marker from "./Marker";
import { Map, MapMarker, CustomOverlayMap, useMap } from "react-kakao-maps-sdk";
import AccountBalanceIcon from "@mui/icons-material/AccountBalance";
import icon from "../../lib/icon/loc7.png";

function KakaoMap({ state, flag }) {
  const mapHeight = window.innerWidth >= 560 ? 560 : window.innerWidth;
  const center = state;
  const [isOpen, setIsOpen] = useState(false);
  const marker = Marker();
  console.log(typeof marker, marker);

  const EventMarkerContainer = ({ position, content }) => {
    const map = useMap();
    const [isVisible, setIsVisible] = useState(false);

    return (
      <MapMarker
        key={`${position.title}-${position.latlng}`}
        position={position.latlng} // 마커를 표시할 위치
        clickable={true}
        onClick={() => setIsOpen(true)}
        image={{
          src: icon,
          size: {
            width: 33,
            height: 33,
          }, // 마커이미지의 크기입니다
        }}
        title={position.title} // 마커의 타이틀, 마커에 마우스를 올리면 타이틀이 표시됩니다
      >
        {/* MapMarker의 자식을 넣어줌으로 해당 자식이 InfoWindow로 만들어지게 합니다 */}
        {/* 인포윈도우에 표출될 내용으로 HTML 문자열이나 React Component가 가능합니다 */}
        {isOpen && (
          <div style={{ minWidth: "150px" }}>
            <img
              alt="close"
              width="14"
              height="13"
              src="https://t1.daumcdn.net/localimg/localimages/07/mapjsapi/2x/bt_close.gif"
              style={{
                position: "absolute",
                right: "5px",
                top: "5px",
                cursor: "pointer",
              }}
              onClick={() => setIsOpen(false)}
            />
            <div style={{ padding: "5px", color: "#000" }}>
              {position.title}
            </div>
          </div>
        )}
      </MapMarker>
    );
  };

  return (
    <div>
      <Map // 지도를 표시할 Container
        center={center}
        style={{
          // 지도의 크기
          width: "100%",
          height: `${mapHeight}px`,
        }}
        level={3} // 지도의 확대 레벨
      >
        {flag && <MapMarker position={center} />}
        {marker.map((position, index) => (
          <EventMarkerContainer
            key={`EventMarkerContainer-${position.latlng.lat}-${position.latlng.lng}`}
            position={position.latlng}
            content={position.title}
          />
          // <MapMarker
          //   key={`${position.title}-${position.latlng}`}
          //   position={position.latlng} // 마커를 표시할 위치
          //   clickable={true}
          //   onClick={() => setIsOpen(true)}
          //   image={{
          //     src: icon,
          //     size: {
          //       width: 33,
          //       height: 33,
          //     }, // 마커이미지의 크기입니다
          //   }}
          //   title={position.title} // 마커의 타이틀, 마커에 마우스를 올리면 타이틀이 표시됩니다
          // >
          //   {/* MapMarker의 자식을 넣어줌으로 해당 자식이 InfoWindow로 만들어지게 합니다 */}
          //   {/* 인포윈도우에 표출될 내용으로 HTML 문자열이나 React Component가 가능합니다 */}
          //   {isOpen && (
          //     <div style={{ minWidth: "150px" }}>
          //       <img
          //         alt="close"
          //         width="14"
          //         height="13"
          //         src="https://t1.daumcdn.net/localimg/localimages/07/mapjsapi/2x/bt_close.gif"
          //         style={{
          //           position: "absolute",
          //           right: "5px",
          //           top: "5px",
          //           cursor: "pointer",
          //         }}
          //         onClick={() => setIsOpen(false)}
          //       />
          //       <div style={{ padding: "5px", color: "#000" }}>
          //         {position.title}
          //       </div>
          //     </div>
          //   )}
          // </MapMarker>
        ))}
      </Map>
    </div>
  );
}

export default React.memo(KakaoMap);
