import React, { useState } from "react";
import Button from "@mui/material/Button";
import ButtonGroup from "@mui/material/ButtonGroup";
import Box from "@mui/material/Box";
import { RenderAfterNavermapsLoaded, NaverMap } from "react-naver-maps";
import Search from "./Search";
import Map from "./Map";
import HashTag from "./HashTag";

function Activate() {
  const [stat, changeStat] = useState(0);

  const activateTextField = () => {
    changeStat(0);
  };
  const activateHashTag = () => {
    changeStat(1);
  };
  const activateMap = () => {
    changeStat(2);
  };

  return (
    <div>
      <Box
        sx={{
          display: "flex",
          flexDirection: "column",
          alignItems: "center",
          "& > *": {
            m: 2,
          },
        }}
      >
        <ButtonGroup
          size="large"
          variant="text"
          aria-label="text button group"
          color="warning"
          sx={{ paddingBottom: "20px" }}
        >
          <Button onClick={activateTextField}>검색</Button>
          <Button onClick={activateHashTag}>해쉬태그</Button>
          <Button onClick={activateMap}>지도</Button>
        </ButtonGroup>
      </Box>

      <div>{stat === 0 && <Search />}</div>
      <div>{stat === 1 && <HashTag />} </div>
      <div>{stat === 2 && <Map />}</div>
      {/*
      <Box
        component="form"
        sx={{
          "& .MuiTextField-root": { m: 1, width: "25ch" },
        }}
        noValidate
        autoComplete="off"
      >
        <ThemeProvider theme={theme}>
            <TextField
            id="standard-search"
            label="검색"
            type="search"
            variant="standard"
            color="secondary"
            focused
            />
        </ThemeProvider>
      </Box>*/}
    </div>
  );
}

export default Activate;
