/*global kakao*/
import React, { useEffect, useRef, useState } from "react";
import Marker from "./Marker";
import icon from "../../lib/icon/loc7.png";

function KakaoMap({ lat, lng, showMarker }) {
  const mapHeight = window.innerWidth >= 560 ? 560 : window.innerWidth;
  const mapContainer = useRef(null);

  const galleriesMarker = Marker();

  const imageSize = new window.kakao.maps.Size(35, 35);
  const galleryMarkerImage = new window.kakao.maps.MarkerImage(icon, imageSize);

  const mapOptions = {
    center: new window.kakao.maps.LatLng(lat, lng),
    level: 6,
  };

  const [overlays, setOverlays] = useState([]);

  const closeOverlay = () => {
    overlays.forEach((overlay) => overlay.setMap(null));
    setOverlays([]);
  };

  useEffect(() => {
    // const container = document.getElementById("map");
    const map = new window.kakao.maps.Map(mapContainer.current, mapOptions);

    if (showMarker) {
      const markerPosition = new window.kakao.maps.LatLng(lat, lng);
      const marker = new window.kakao.maps.Marker({
        map: map,
        position: markerPosition,
      });
    }

    // galleriesMarker.map((galleryMarker) => {
    //   const setGalleryMarker = new window.kakao.maps.Marker({
    //     map: map,
    //     position: new window.kakao.maps.LatLng(
    //       galleryMarker.latlng.lat,
    //       galleryMarker.latlng.lng
    //     ),
    //     image: galleryMarkerImage,
    //   });

    //   const overlayContent =
    //     `<div>${galleryMarker.title}</div>` +
    //     `<div class="close" onclick="closeOverlay()" title="닫기">hey</div>`;

    //   const overlay = new window.kakao.maps.CustomOverlay({
    //     content: overlayContent,
    //     position: setGalleryMarker.getPosition(),
    //   });

    //   const closeOverlay = () => {
    //     overlay.setMap(null);
    //   };
    //   window[`closeOverlay_${galleryMarker.id}`] = closeOverlay;

    //   window.kakao.maps.event.addListener(
    //     setGalleryMarker,
    //     "click",
    //     function () {
    //       overlay.setMap(map);
    //     }
    //   );
    //   window.closeOverlay = closeOverlay;
    // });
    galleriesMarker.map((galleryMarker) => {
      const setGalleryMarker = new window.kakao.maps.Marker({
        map: map,
        position: new window.kakao.maps.LatLng(
          galleryMarker.latlng.lat,
          galleryMarker.latlng.lng
        ),
        image: galleryMarkerImage,
      });

      const overlayContent = `<div>${galleryMarker.title}</div>`;
      const overlay = new window.kakao.maps.CustomOverlay({
        content: overlayContent,
        position: setGalleryMarker.getPosition(),
      });

      window.kakao.maps.event.addListener(
        setGalleryMarker,
        "click",
        function () {
          closeOverlay(); // Close any active overlays
          overlay.setMap(map);
          setOverlays([overlay]);
        }
      );

      return overlay;
    });
  }, [lat, lng, showMarker]);

  return (
    <div>
      <div
        id="map"
        ref={mapContainer}
        style={{ width: "100%", height: `${mapHeight}px` }}
      ></div>
    </div>
  );
}

export default React.memo(KakaoMap);
